inline QByteArray UpdatesType::getHash(QCryptographicHash::Algorithm alg) const {
    QByteArray data;
    QDataStream str(&data, QIODevice::WriteOnly);
    str << *this;
    return QCryptographicHash::hash(data, alg);
}

inline QDataStream &operator<<(QDataStream &stream, const UpdatesType &item) {
    stream << static_cast<uint>(item.classType());
    switch(item.classType()) {
    case UpdatesType::typeUpdatesTooLong:
        
        break;
    case UpdatesType::typeUpdateShortMessage:
        stream << item.flags();
        stream << item.id();
        stream << item.userId();
        stream << item.message();
        stream << item.pts();
        stream << item.ptsCount();
        stream << item.date();
        stream << item.fwdFrom();
        stream << item.viaBotId();
        stream << item.replyToMsgId();
        stream << item.entities();
        break;
    case UpdatesType::typeUpdateShortChatMessage:
        stream << item.flags();
        stream << item.id();
        stream << item.fromId();
        stream << item.chatId();
        stream << item.message();
        stream << item.pts();
        stream << item.ptsCount();
        stream << item.date();
        stream << item.fwdFrom();
        stream << item.viaBotId();
        stream << item.replyToMsgId();
        stream << item.entities();
        break;
    case UpdatesType::typeUpdateShort:
        stream << item.update();
        stream << item.date();
        break;
    case UpdatesType::typeUpdatesCombined:
        stream << item.updates();
        stream << item.users();
        stream << item.chats();
        stream << item.date();
        stream << item.seqStart();
        stream << item.seq();
        break;
    case UpdatesType::typeUpdates:
        stream << item.updates();
        stream << item.users();
        stream << item.chats();
        stream << item.date();
        stream << item.seq();
        break;
    case UpdatesType::typeUpdateShortSentMessage:
        stream << item.flags();
        stream << item.id();
        stream << item.pts();
        stream << item.ptsCount();
        stream << item.date();
        stream << item.media();
        stream << item.entities();
        break;
    }
    return stream;
}

